/**
 * CPSOAuthBehavior provides OAuth support to any Pogostick component
 *
 * Introduces new event: onUserAuthorized raised when a user has been authorized
 *
 * @package	 psYiiExtensions
 * @subpackage	 behaviors
 *
 * @author		 Jerry Ablan <jablan@pogostick.com>
 * @version	 SVN: $Id: CPSOAuthBehavior.php 358 2010-01-02 23:33:40Z jerryablan@gmail.com $
 * @since		 v1.0.0
 *
 * @filesource
 *
 * @property-read OAuth $oAuthObject The curent OAuth object
 * @property-read string $token The current token
 */
class CPSOAuthBehavior extends CPSComponentBehavior
{
	//********************************************************************************
	//* Constants
	//********************************************************************************

	/***
	 * Our OAuth object
	 * @var OAuth
	 */
	protected $_oauth = null;

	/**
	 * The current token
	 * @var array
	 */
	protected $_currentToken = null;

	/**
	 * @var string
	 */
	protected $_callbackUrl;
	/**
	 * @var bool
	 */
	protected $_isAuthorized = false;
	/**
	 * @var string
	 */
	protected $_accessTokenUrl = '/oauth/access_token';
	/**
	 * @var string
	 */
	protected $_authorizeUrl = '/oauth/authorize';
	/**
	 * @var string
	 */
	protected $_requestTokenUrl = '/oauth/request_token';

	//********************************************************************************
	//* Properties
	//********************************************************************************

	/**
	 * Retrieves the current token
	 * @return array
	 */
	public function getToken( )
	{
		return $this->_currentToken;
	}

	/**
	 * Retrieves the OAuth object
	 * @return oauth
	 */
	public function getOAuthObject( )
	{
		return $this->_oauth;
	}

	//********************************************************************************
	//* Constructor
	//********************************************************************************

	/***
	 * Constructor
	 *
	 */
	public function __construct( )
	{
		//	No oauth? No run...
		if ( !extension_loaded( 'oauth' ) )
				{
					throw new CException( Yii::t( 'psOAuthBehavior', 'The "oauth" extension is not loaded. Please install
			and/or load the oath extension (PECL).' ) );
				}

		//	Call daddy...
		parent::__construct( );
	}

	//********************************************************************************
	//* Public Methods
	//********************************************************************************

	/**
	 * Initialize this behavior
	 *
	 */
	public function init( )
	{
		parent::init( );

		//	Events...
		$this->attachEventHandler( 'onUserAuthorized', array( $this, 'userAuthorized' ) );

		//	Create our object...
		$this->_oauth = new OAuth(
			$this->_apiKey,
			$this->_apiSecretKey,
			OAUTH_SIG_METHOD_HMACSHA1,
			OAUTH_AUTH_TYPE_URI
		);

		//	Load any tokens we have...
		$this->loadToken( );

		//	Have we been authenticated?
		if ( !$this->isAuthorized )
		{
			if ( isset( $_REQUEST['oauth_token'] ) )
			{
				if ( $this->_oauth->setToken( $_REQUEST['oauth_token'], $_REQUEST['oauth_verifier'] ) )
				{
					$_arToken = $this->_oauth->getAccessToken(
						$this->apiBaseUrl . $this->accessTokenUrl, null, $_REQUEST['oauth_verifier'] );
					$this->storeToken( $_arToken );
					$this->isAuthorized = true;
				}

				//	Raise our event
				if ( $this->isAuthorized )
					$this->onUserAuthorized( new CPSOAuthEvent( $this->_currentToken ) );
			}
		}
	}

	/**										 I
	 * Appends the current token to the authorizeUrl option
	 *
	 * @param mixed $oToken
	 */
	public function getAuthorizeUrl( )
	{
		$_arToken = $this->_oauth->getRequestToken( $this->apiBaseUrl . $this->requestTokenUrl, $this->callbackUrl );
		return $this->apiBaseUrl . $this->authorizeUrl . '?oauth_token=' . $_arToken['oauth_token'];
	}

	/**
	 * Stores the current token in a member variable and in the user state oAuthToken
	 *
	 * @param array $oToken
	 */
	public function storeToken( $oToken = array() )
	{
		try
		{
			Yii::app( )->user->setState( $this->getInternalName( ) . '_oAuthToken', $oToken );
			Yii::app( )->user->setState( $this->getInternalName( ) . '_isAuthorized', $this->isAuthorized );
			$this->_currentToken = $oToken;
		}
		catch ( Exception $_ex )
		{
			$_sName = $this->getInternalName( );
			CPSLog::error( 'pogostick.behaviors', Yii::t( $_sName, 'Error storing OAuth token "{a}/{b}" : {c}', array(












































































																													 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 	 